import React, { useState } from 'react';
import ToDoList from './ToDoList';

function App() {
  const [tasks, setTasks] = useState([
    'Do laundry',
    'Go to gym',
    'Walk dog',
  ]);

  // Function to add a new task
  const addTask = () => {
    setTasks([...tasks, 'New Task']);
  };

  return (
    <div>
      <h1>My To-Do List</h1>
      <button onClick={addTask}>Add Task</button>
      <ToDoList tasks={tasks} />
    </div>
  );
}

export default App;
